import React, { useState, useEffect, useRef } from 'react';
import ReactMarkdown from 'react-markdown';

const ChatbotUI = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const effectRan = useRef(false);

  const sendMessage = async (message) => {
    // ... (sendMessage function remains the same)
  };

  useEffect(() => {
    if (effectRan.current === false) {
      sendMessage('Hello, AI Assistant!');
      return () => effectRan.current = true;
    }
  }, []);

  return (
    <div className="flex flex-col min-h-screen">
      {/* Header */}
      <header className="bg-gray-100 p-4 text-center">
        <h1 className="text-2xl font-bold">Your Logo Here</h1>
      </header>

      {/* Main content */}
      <div className="flex flex-1 p-4">
        {/* Left sidebar */}
        <aside className="w-1/4 pr-4">
          <h2 className="text-xl font-semibold mb-2">Left Sidebar</h2>
          <p>You can add text or other content here.</p>
        </aside>

        {/* Chatbot UI */}
        <main className="w-1/2 px-4 border-l border-r">
          <h2 className="text-2xl font-bold mb-4">AI Assistant</h2>
          <div className="h-96 overflow-y-auto mb-4 border rounded p-2">
            {messages.map((message, index) => (
              <div key={index} className={`mb-2 ${message.sender === 'user' ? 'text-right' : 'text-left'}`}>
                <span className={`inline-block p-2 rounded-lg ${message.sender === 'user' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}>
                  <ReactMarkdown>{message.text}</ReactMarkdown>
                </span>
              </div>
            ))}
          </div>
          <div className="flex">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && !isLoading && input.trim() && sendMessage(input)}
              placeholder="Type a message..."
              className="flex-1 p-2 border rounded-l"
              disabled={isLoading}
            />
            <button
              onClick={() => !isLoading && input.trim() && sendMessage(input)}
              className="p-2 bg-blue-500 text-white rounded-r"
              disabled={isLoading}
            >
              Send
            </button>
          </div>
        </main>

        {/* Right sidebar */}
        <aside className="w-1/4 pl-4">
          <h2 className="text-xl font-semibold mb-2">Right Sidebar</h2>
          <p>You can add more text or content here.</p>
        </aside>
      </div>

      {/* Footer */}
      <footer className="bg-gray-100 p-4 text-center">
        <p>Footer content goes here</p>
      </footer>
    </div>
  );
};

export default ChatbotUI;
