import React, { useState, useEffect, useRef } from 'react';
import ReactMarkdown from 'react-markdown';

const ChatbotUI = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const effectRan = useRef(false);

  const sendMessage = async (message) => {
    // ... (sendMessage function implementation remains unchanged)
  };

  useEffect(() => {
    if (effectRan.current === false) {
      sendMessage('Hello, AI Assistant!');
      return () => effectRan.current = true;
    }
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-7xl mx-auto space-y-6">
        {/* Top box */}
        <div className="bg-white p-6 rounded-lg shadow-md">
          <h1 className="text-3xl font-bold mb-2">Welcome to Our AI Assistant</h1>
          <p className="text-gray-600">This is the top content area where you can add introductory text or other information.</p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Left box */}
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-2xl font-semibold mb-4">Left Box</h2>
            <p className="text-gray-600">You can add navigation, additional info, or other content here.</p>
          </div>

          {/* Chatbot UI (Center box) */}
          <div className="bg-white rounded-lg shadow-md overflow-hidden flex flex-col lg:col-span-1">
            <h2 className="text-2xl font-semibold p-4 bg-blue-500 text-white">AI Assistant</h2>
            <div className="flex-1 overflow-y-auto p-4 max-h-[50vh] min-h-[300px]">
              {messages.map((message, index) => (
                <div key={index} className={`mb-4 ${message.sender === 'user' ? 'text-right' : 'text-left'}`}>
                  <span className={`inline-block p-3 rounded-lg ${message.sender === 'user' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}>
                    {message.sender === 'user' ? (
                      message.text
                    ) : (
                      <ReactMarkdown>{message.text}</ReactMarkdown>
                    )}
                  </span>
                </div>
              ))}
            </div>
            <div className="p-4 bg-gray-100">
              <div className="flex">
                <input
                  type="text"
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && !isLoading && input.trim() && sendMessage(input)}
                  placeholder="Type a message..."
                  className="flex-1 p-2 border rounded-l-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  disabled={isLoading}
                />
                <button
                  onClick={() => !isLoading && input.trim() && sendMessage(input)}
                  className="p-2 bg-blue-500 text-white rounded-r-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-700"
                  disabled={isLoading}
                >
                  Send
                </button>
              </div>
            </div>
          </div>

          {/* Right box */}
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h2 className="text-2xl font-semibold mb-4">Right Box</h2>
            <p className="text-gray-600">This area can be used for additional features, settings, or other relevant information.</p>
          </div>
        </div>

        {/* Bottom box */}
        <div className="bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-2xl font-semibold mb-4">Additional Information</h2>
          <p className="text-gray-600">You can add more content, disclaimers, or any other relevant information here.</p>
        </div>
      </div>
    </div>
  );
};

export default ChatbotUI;
