import React, { useState } from 'react';
import { Send, Bot, User } from 'lucide-react';
import ReactMarkdown from 'react-markdown';

const ChatbotUI = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [currentResponse, setCurrentResponse] = useState('');

  const sendMessage = async (message) => {
    setIsLoading(true);
    setMessages(prevMessages => [...prevMessages, { text: message, sender: 'user' }]);
    setCurrentResponse('');

    try {
      const response = await fetch('http://localhost:3001/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message }),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const reader = response.body.getReader();
      const decoder = new TextDecoder();

      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        const chunk = decoder.decode(value);
        const lines = chunk.split('\n\n');

        for (const line of lines) {
          if (line.startsWith('data: ')) {
            const eventData = JSON.parse(line.slice(6));
            if (eventData.text) {
              setCurrentResponse(prev => prev + eventData.text);
            } else if (eventData.fullResponse) {
              setMessages(prevMessages => [
                ...prevMessages,
                { 
                  text: eventData.fullResponse, 
                  sender: 'bot',
                  citations: eventData.citations 
                }
              ]);
              setCurrentResponse('');
            }
          }
        }
      }
    } catch (error) {
      console.error('Error:', error);
      setMessages(prevMessages => [
        ...prevMessages,
        { text: "Sorry, there was an error processing your request.", sender: 'bot' }
      ]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSend = async () => {
    if (input.trim()) {
      setInput('');
      await sendMessage(input);
    }
  };

  return (
    <div className="flex flex-col h-screen max-w-2xl mx-auto">
      <div className="bg-blue-600 text-white p-4">
        <h1 className="text-2xl font-bold">AI Assistant</h1>
      </div>
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages.map((message, index) => (
          <div key={index} className={`flex ${message.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
            <div className={`flex items-start space-x-2 ${message.sender === 'user' ? 'flex-row-reverse' : ''}`}>
              {message.sender === 'user' ? (
                <User className="w-8 h-8 rounded-full bg-blue-100 p-1" />
              ) : (
                <Bot className="w-8 h-8 rounded-full bg-green-100 p-1" />
              )}
              <div className={`rounded-lg p-3 ${message.sender === 'user' ? 'bg-blue-100' : 'bg-green-100'}`}>
                {message.sender === 'user' ? (
                  message.text
                ) : (
                  <>
                    <ReactMarkdown>{message.text}</ReactMarkdown>
                    {message.citations && message.citations.length > 0 && (
                      <div className="mt-2 text-sm text-gray-600">
                        <strong>Citations:</strong>
                        <ul>
                          {message.citations.map((citation, idx) => (
                            <li key={idx}>{citation}</li>
                          ))}
                        </ul>
                      </div>
                    )}
                  </>
                )}
              </div>
            </div>
          </div>
        ))}
        {currentResponse && (
          <div className="flex justify-start">
            <div className="rounded-lg p-3 bg-green-100">
              <ReactMarkdown>{currentResponse}</ReactMarkdown>
            </div>
          </div>
        )}
        {isLoading && !currentResponse && (
          <div className="flex justify-start">
            <div className="bg-gray-200 rounded-lg p-3">
              <span className="animate-pulse">AI is thinking...</span>
            </div>
          </div>
        )}
      </div>
      <div className="border-t p-4">
        <div className="flex space-x-2">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSend()}
            placeholder="Type a message..."
            className="flex-1 border rounded-full px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button 
            onClick={handleSend} 
            className="bg-blue-500 text-white rounded-full p-2 hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
            disabled={isLoading}
          >
            <Send className="w-6 h-6" />
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatbotUI;
