import React, { useState, useEffect, useRef } from 'react';
import ReactMarkdown from 'react-markdown';

const ChatbotUI = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const effectRan = useRef(false);

  const sendMessage = async (message) => {
    if (message.trim() === '') return;

    setIsLoading(true);
    setMessages(prevMessages => [...prevMessages, { text: message, sender: 'user' }]);
    setInput('');

    // Simulating an API call
    setTimeout(() => {
      const botReply = "This is a simulated response from the AI.";
      setMessages(prevMessages => [...prevMessages, { text: botReply, sender: 'bot' }]);
      setIsLoading(false);
    }, 1000);
  };

  useEffect(() => {
    if (effectRan.current === false) {
      sendMessage('Hello, AI Assistant!');
      return () => effectRan.current = true;
    }
  }, []);

  return (
    <div className="flex flex-col h-screen">
      {/* Top bar */}
      <div className="bg-gray-200 p-4">
        <h1 className="text-2xl font-bold text-center">Top Bar</h1>
      </div>

      {/* Main content area */}
      <div className="flex flex-1 overflow-hidden">
        {/* Left sidebar */}
        <div className="w-64 bg-gray-100 p-4 overflow-y-auto">
          <h2 className="text-xl font-semibold mb-2">Left Sidebar</h2>
          <p>You can add content here.</p>
        </div>

        {/* Chatbot UI */}
        <main className="flex-1 flex flex-col p-4 overflow-hidden">
          <h2 className="text-2xl font-bold mb-4 text-center">AI Assistant</h2>
          <div className="flex-1 overflow-y-auto mb-4 p-4 border rounded-lg bg-white">
            {messages.map((message, index) => (
              <div key={index} className={`mb-4 ${message.sender === 'user' ? 'text-right' : 'text-left'}`}>
                <span className={`inline-block p-3 rounded-lg ${message.sender === 'user' ? 'bg-blue-100' : 'bg-gray-200'} max-w-[80%]`}>
                  <ReactMarkdown className="break-words">{message.text}</ReactMarkdown>
                </span>
              </div>
            ))}
          </div>
          <div className="flex">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && !isLoading && sendMessage(input)}
              placeholder="Type a message..."
              className="flex-1 p-2 border rounded-l-lg"
              disabled={isLoading}
            />
            <button
              onClick={() => !isLoading && sendMessage(input)}
              className="p-2 bg-blue-500 text-white rounded-r-lg"
              disabled={isLoading}
            >
              Send
            </button>
          </div>
        </main>

        {/* Right sidebar */}
        <div className="w-64 bg-gray-100 p-4 overflow-y-auto">
          <h2 className="text-xl font-semibold mb-2">Right Sidebar</h2>
          <p>You can add more content here.</p>
        </div>
      </div>

      {/* Bottom bar */}
      <div className="bg-gray-200 p-4">
        <p className="text-center">Bottom Bar</p>
      </div>
    </div>
  );
};

export default ChatbotUI;
