import React, { useState, useEffect, useRef } from 'react';
import ReactMarkdown from 'react-markdown';

const ChatbotUI = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const effectRan = useRef(false);

  const sendMessage = async (message) => {
    if (message.trim() === '') return;

    setIsLoading(true);
    setMessages(prevMessages => [...prevMessages, { text: message, sender: 'user' }]);
    setInput('');

    try {
      const response = await fetch('http://localhost:3001/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message }),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const reader = response.body.getReader();
      const decoder = new TextDecoder();

      while (true) {
        const { done, value } = await reader.read();
        if (done) {
          console.log('Stream complete');
          break;
        }

        const chunk = decoder.decode(value);
        console.log('Received chunk:', chunk);
        const lines = chunk.split('\n');

        for (const line of lines) {
          if (line.startsWith('data: ')) {
            try {
              const eventData = JSON.parse(line.slice(6));
              console.log('Parsed event data:', eventData);
              if (eventData.fullResponse) {
                setMessages(prevMessages => [
                  ...prevMessages,
                  {
                    text: eventData.fullResponse,
                    sender: 'bot',
                    citations: eventData.citations
                  }
                ]);
              }
            } catch (error) {
              console.error('Error parsing event data:', error);
            }
          }
        }
      }
    } catch (error) {
      console.error('Error:', error);
      setMessages(prevMessages => [
        ...prevMessages,
        { text: "Sorry, there was an error processing your request.", sender: 'bot' }
      ]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  const handleSendMessage = () => {
    if (!isLoading && input.trim()) {
      sendMessage(input);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !isLoading && input.trim()) {
      sendMessage(input);
    }
  };

  useEffect(() => {
    if (effectRan.current === false) {
      sendMessage('Hello, AI Assistant!');
      return () => effectRan.current = true;
    }
  }, []);

  return (
    <div className="flex flex-col h-screen p-4 font-sans">
      <div className="mb-4 text-center">
        <h1 className="text-2xl font-bold">Your Logo Here</h1>
      </div>

      <div className="flex mb-4 flex-1">
        <div className="w-1/4 pr-4">
          <h2 className="text-lg font-bold mb-2">Left Sidebar</h2>
          <p>You can add text or other content here.</p>
        </div>

        <div className="w-1/2 flex flex-col">
          <h2 className="text-xl font-bold mb-4 text-center">AI Assistant</h2>
          <div className="flex-1 border border-gray-300 rounded-lg overflow-y-auto mb-4 p-4">
            {messages.map((message, index) => (
              <div key={index} className={`mb-2 ${message.sender === 'user' ? 'text-right' : 'text-left'}`}>
                <span className={`inline-block p-2 rounded-lg ${message.sender === 'user' ? 'bg-blue-100' : 'bg-gray-100'} max-w-[80%]`}>
                  <ReactMarkdown>{message.text}</ReactMarkdown>
                  {message.citations && message.citations.length > 0 && (
                    <div className="mt-2 text-sm text-gray-600">
                      <strong>Citations:</strong>
                      <ol className="pl-5">
                        {message.citations.map((citation, idx) => {
                          const displayUrl = citation.mappedUrl || citation.originalUrl || citation.url;
                          return (
                            <li key={idx}>
                              {displayUrl ? (
                                <a
                                  href={displayUrl}
                                  target="_blank"
                                  rel="noopener noreferrer"
                                  className="text-blue-500 hover:underline"
                                >
                                  {citation.title || displayUrl}
                                </a>
                              ) : (
                                <span>{citation.title || 'Untitled Citation'}</span>
                              )}
                              {citation.error && ` (${citation.error})`}
                            </li>
                          );
                        })}
                      </ol>
                    </div>
                  )}
                </span>
              </div>
            ))}
          </div>
          <div className="flex">
            <input
              type="text"
              value={input}
              onChange={handleInputChange}
              onKeyPress={handleKeyPress}
              placeholder="Type a message..."
              className="flex-1 p-2 border rounded-l-lg"
              disabled={isLoading}
            />
            <button
              onClick={handleSendMessage}
              className="p-2 bg-blue-500 text-white rounded-r-lg hover:bg-blue-600"
              disabled={isLoading}
            >
              Send
            </button>
          </div>
        </div>

        <div className="w-1/4 pl-4">
          <h2 className="text-lg font-bold mb-2">Right Sidebar</h2>
          <p>You can add more text or content here.</p>
        </div>
      </div>

      <div className="text-center">
        <p>Footer content goes here</p>
      </div>
    </div>
  );
};

export default ChatbotUI;
